sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var body = request.body.data;
	var zendutyEscalationPolicy = body.zenduty_escalation_policy;
    var assignmentGroupId = body.snow_ag_id;
    var assignmentGroupName = body.snow_ag_name;
	var action = body.action;
	var previousAssignmentGroupId = body.previous_assignment_group_unique_id;

	if (!action) {
        return errorResponse("Invalid payload action is missing");
    }

	if (action=="update" && !previousAssignmentGroupId) {
        return errorResponse("Invalid payload previous_assignment_group_id is missing");
    }

	if (!zendutyEscalationPolicy) {
        return errorResponse("Invalid payload zenduty escalation policy is missing");
    }
    if (!assignmentGroupId || !assignmentGroupName) {
        return errorResponse("Both assignment_group_id and assignment_group_name are required.");
    }

    // === Assignment Group Validation ===
    var agGR = new GlideRecord('sys_user_group');
    agGR.addQuery('sys_id', assignmentGroupId);
    agGR.addQuery('name', assignmentGroupName);
    agGR.query();
    if (!agGR.next()) {
        return errorResponse("Invalid Assignment Group: no matching record.");
    }

	// === Unmap Zenduty Integration from Previous Configuration Item ===
	if (action === "update") {
		var previousAGGR = new GlideRecord('sys_user_group');
		previousAGGR.addQuery('sys_id', previousAssignmentGroupId);
		previousAGGR.query();

		if (previousAGGR.next()) {
			previousAGGR.u_zenduty_escalation_policy = ''; // Clear the custom field
			previousAGGR.update();
		}
	}

	// === Mapping to Custom Fields ===
    try {
        // Update Assignment Group with ZD EP
        agGR.u_zenduty_escalation_policy = zendutyEscalationPolicy;
        agGR.update();

    } catch (e) {
        return errorResponse("Mapping failed: " + e.message);
    }

    // âœ… All Passed
    response.setStatus(200);
    response.setBody({
        success: true,
        message: "Mapping successful."
    });

    function errorResponse(msg) {
        response.setStatus(400);
        response.setBody({ success: false, error: msg });
        return;
    }

})(request, response);
