sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var requestBody = request.body.data;

    var incidentSysId = requestBody.incident_sys_id;
    var workNote = requestBody.work_note;

    if (!incidentSysId || !workNote) {
        response.setStatus(400);
        response.setBody({ result: 'Missing required fields: incident_sys_id or work_note' });
        return;
    }

    var incidentGR = new GlideRecord('incident');
    if (!incidentGR.get(incidentSysId)) {
        response.setStatus(404);
        response.setBody({ result: 'Incident not found with given sys_id' });
        return;
    }

	var journalEntry = new GlideRecord('sys_journal_field');
	journalEntry.initialize();
	journalEntry.name = "incident";
	journalEntry.element_id = incidentSysId;
	journalEntry.element = 'work_notes';
	journalEntry.value = workNote;
	journalEntry.u_originated_from = "zenduty";
	var noteSysId = journalEntry.insert();

	var originalDescription = incidentGR.getValue('description');
	incidentGR.setValue('description', originalDescription + ".");
    incidentGR.update();

	incidentGR.setValue('description', originalDescription);
	incidentGR.update();

    response.setStatus(200);
	response.setBody({
		message: 'Work note added successfully',
		sys_id: noteSysId
	});
})(request, response);
