sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var body = request.body.data;
	var sysID = body.sys_id;
    var zendutyIncident = body.zenduty_incident;

    if (!body || !sysID || !zendutyIncident) {
        return errorResponse("Invalid Payload");
    }

    // === Incident Validation ===
    var incidentGR = new GlideRecord('incident');
    incidentGR.addQuery('sys_id', sysID);
    incidentGR.query();
    if (!incidentGR.next()) {
        return errorResponse("Invalid incident sys id.");
    }

	// === Mapping to Custom Fields ===
    try {
        incidentGR.u_zenduty_incident = zendutyIncident;
        incidentGR.update();

    } catch (e) {
        return errorResponse("Mapping failed: " + e.message);
    }

    // âœ… All Passed
    response.setStatus(200);
    response.setBody({
        success: true,
        message: "Mapping successful."
    });

    function errorResponse(msg) {
        response.setStatus(400);
        response.setBody({ success: false, error: msg });
        return;
    }

})(request, response);
